import React from "react";
import Button from "./Button";


function QuoteBox({quote, author,background}) {
    return (
        <div className='quoteBox'>
            <h2>{quote}</h2>
            <h3>{author}</h3>
            <i
            className="quotes"
            class="fas fa-quote-left"
            style={{ color: background }}
          ></i>
        </div>
        
    );
}

export default QuoteBox;
